function [C, S, GM, R, n]=read_coeff(fid)% Einlesen der Koeffizienten in .gfc Datei% input:  fid file id .gfc Datei% output: C,S Koeffizienten Clm und Slm%         GM  Gravitiationskonstante*Masse der Erde%         R   Erdradius in m %         n   Maximaler Grad der Kugelfunktionsentwicklung%% Headerwhile 1  tline=fgetl(fid);  if strncmp('earth_gravity_constant',tline,10)    GM=strread(tline,'%*s %f');  elseif strncmp('radius',tline,5)    R=strread(tline,'%*s %f');  elseif strncmp('max_degree',tline,8)    n=strread(tline,'%*s %f');  elseif strncmp('end_of_head',tline,10)    break  endend%% Datadata=textscan(fid,'%*s %f %f %f %f %f %f ');data=cell2mat(data);%% Koeffizienten in n+1 x n+1 MatrixS=zeros(n+1,n+1);C=S;for hi=1:length(data)  C(data(hi,1)+1,data(hi,2)+1)=data(hi,3);  S(data(hi,1)+1,data(hi,2)+1)=data(hi,4);endend